import { USER_LOGIN_FAIL, USER_LOGIN_REQUEST, USER_LOGIN_SUCCESS, USER_LOGOUT, USER_REGISTER_REQUEST, USER_REGISTER_SUCCESS, USER_REGISTER_FAIL, USER_DETAILS_REQUEST, USER_DETAILS_SUCCESS, USER_DETAILS_FAIL, USER_DETAILS_RESET, USER_UPDATE_PROFILE_RESET, USER_UPDATE_PROFILE_SUCCESS,USER_UPDATE_PROFILE_REQUEST,USER_UPDATE_PROFILE_FAIL  } from './../Constants/UserConstants';


//LOGIN

export const userLoginReducer = (state = {}, action) =>{
    switch (action.type) {

        case USER_LOGIN_REQUEST:
            return {loading: true};
        case USER_LOGIN_SUCCESS:
            return {loading: false, userInfo: action.payload};
        case USER_LOGIN_FAIL:
            return {loading: false, error: action.payload };
        case USER_LOGOUT:
            return {};
                
            
    
        default:
            return state;
    }
  
}

//Register
export const userRegisterReducer = (state = {}, action) =>{
    switch (action.type) {

        case USER_REGISTER_REQUEST:
            return {loading: true};
        case USER_REGISTER_SUCCESS:
            return {loading: false, userInfo: action.payload};
        case USER_REGISTER_FAIL:
            return {loading: false, error: action.payload };
       
        default:
            return state;
    }
}

//USER DEATAILS
export const userDetailsReducer = (state = {}, action) =>{
    switch (action.type) {

        case USER_DETAILS_REQUEST:
            return {loading: true};
        case USER_DETAILS_SUCCESS:
            return {loading: false, user: action.payload};
        case USER_DETAILS_FAIL:
            return {loading: false, error: action.payload };
        case USER_DETAILS_RESET:
            return {user: {}};

        default:
            return state;
    }
}

//USER update profile 
export const userUpdateProfileReducer = (state = {}, action) =>{
    switch (action.type) {

        case USER_UPDATE_PROFILE_REQUEST:
            return {loading: true};
        case USER_UPDATE_PROFILE_SUCCESS:
            return {loading: false, user: action.payload};
        case USER_UPDATE_PROFILE_FAIL:
            return {loading: false, error: action.payload };
        case USER_UPDATE_PROFILE_RESET:
            return {user: {}};

        default:
            return state;
    }
}
