import React, { useState } from "react";
import { Link } from "react-router-dom";
import Header from "../components/Header";

const ShippingScreen = () => {
  window.scrollTo(0, 0);

  const [address, setAddress] = useState("")
  const [city, setCity] = useState("")  
  const [postalCode, setPostalCode] = useState("")
  const [country, setCountry] = useState("")




  const submitHandler = (e) => {
    e.preventDefault();
  };
  return (
    <>
      <Header />
      <div className="container d-flex justify-content-center align-items-center login-center">
        <form
          className="Login col-md-8 col-lg-4 col-11"
          onSubmit={submitHandler}
        >
          <h6>DELIVERY ADDRESS</h6>
          <input type="text" placeholder="Enter address" value={address} required onChange={(e)=> setAddress(e.target.value)} />
          <input type="text" placeholder="Enter city" value={city} required  onChange={(e)=> setCity(e.target.value)}/>
          <input type="text" placeholder="Enter postal code" value={postalCode} required onChange={(e)=> setPostalCode(e.target.value)} />
          <input type="text" placeholder="Enter country" value={country} required onChange={(e)=> setCountry(e.target.value)} />
          <button type="submit">
            <Link to="/payment" className="text-white">
              Continue
            </Link>
          </button>
        </form>
      </div>
    </>
  );
};

export default ShippingScreen;
