import React,{useState,useEffect} from "react";
import { useDispatch } from 'react-redux';
import { useSelector } from 'react-redux';
import Loading from './../LoadingError/Loading';

const ProfileTabs = () => {
  const [name,setName] = useState("");
  const [email,setEmail] = useState("");
  const [password,setPassword] = useState("");
  const [confirmPassword,setConfirmPassword] = useState("");

  const dispatch = useDispatch();

  const userDetails = useSelector((state)=> state.userDetails);
  const {Loading, error, user } = userDetails;

  useEffect(()=>{
    if (user) {
      setName(user.name);
      setEmail(user.email);
    }

  },[dispatch, user] );

  const submitHandler = (e) =>{
    e.preventDefault(); 
    //password match 
    if(password !== confirmPassword){
      alert("password not match ")

    }else{
    }
  }


  return (
    <>
      <form className="row  form-container" onSubmit={submitHandler}>
        <div className="col-md-6">
          <div className="form">
            <label for="account-fn">UserName</label>
            <input className="form-control" type="text" required value={name} onChange={(e)=>  setName(e.target.value)} />
          </div>
        </div>

        <div className="col-md-6">
          <div className="form">
            <label for="account-email">E-mail Address</label>
            <input className="form-control" type="email" value={email} onChange={(e)=>  setEmail(e.target.value)} />
          </div>
        </div>
        <div className="col-md-6">
          <div className="form">
            <label for="account-pass">New Password</label>
            <input className="form-control" type="password" value={password} onChange={(e)=>  setPassword(e.target.value)} />
          </div>
        </div>
        <div className="col-md-6">
          <div className="form">
            <label for="account-confirm-pass">Confirm Password</label>
            <input className="form-control" type="password" value={confirmPassword} onChange={(e)=>  setConfirmPassword(e.target.value)} />
          </div>
        </div>
        <button type="submit">Update Profile</button>
      </form>
    </>
  );
};

export default ProfileTabs;
